import { reqMyOrderList } from "@/api";
export default {
  name: "",
  data() {
    return {
      //收集的是当前页码数
      page: 1,
      //收集的是一页几条数据
      limit: 3,
      //存储我的订单数据
      myorder: {},
      //分页器一共展示多少条数据
      total: 0
    };
  },
  mounted() {
    //组件挂载完毕先获取一次我的订单的数据
    this.getData();
  },
  methods: {
    //获取我的订单数据的方法
    async getData() {
      const {
        page,
        limit
      } = this;
      let result = await reqMyOrderList(page, limit);
      if (result.code == 200) {
        this.myorder = result.data;
      }
    },
    //获取用户点击当前页码的自定义事件的回调
    currentPage(page) {
      //修改参数
      this.page = page;
      this.getData();
    }
  }
};
