import "core-js/modules/es.array.push.js";
export default {
  name: "Login",
  data() {
    return {
      //收集账号与密码
      phone: "",
      password: ""
    };
  },
  methods: {
    //登录按钮
    async login() {
      //整理参数
      const {
        phone,
        password
      } = this;
      //在发登录请求
      try {
        //登录成功
        await this.$store.dispatch("userLogin", {
          phone,
          password
        });
        let topath = this.$route.query.redirect || "/home";
        //跳转到首页
        this.$router.push(topath);
      } catch (error) {
        alert(error.message);
      }
    }
  }
};
